mapper.insert:
			INSERT
			  INTO GAME_RESULT A
			 (
  				  A.RESULT_NUM
 				, A.RESULT_SCORE
 				, A.MEMBER_NUM
 				, A.OPEN_YN
 				, A.USER_NAME
 				, A.GRADE_KIND
 				, A.PLAY_TIME
 				, A.GOLD_SCORE
			 ) 
			 VALUES
			(
			  RESULT_NUM.NEXTVAL
			, #{ score }
			, MEMBER_NUM.NEXTVAL
			, #{ openYn }
			, #{ userName }
			, #{ difficulty }
			, #{ playTime }
			, #{ goldScore }
			)
